import { Component } from '@angular/core';
import { Book } from 'src/app/models/book.model';
import { BookService } from 'src/app/services/book.service';

@Component({
  selector: 'app-add-book',
  templateUrl: './add-book.component.html',
  styleUrls: ['./add-book.component.css'],
})
export class AddBookComponent {
  book: Book = {
    title: '',
    category: '',
    isAvailable: false
  };
  submitted = false;

  constructor(private bookService: BookService) {}

  saveBook(): void {
    const data = {
      title: this.book.title,
      categoty: this.book.category
    };

    this.bookService.create(data).subscribe({
      next: (res) => {
        console.log(res);
        this.submitted = true;
      },
      error: (e) => console.error(e)
    });
  }

  newBook(): void {
    this.submitted = false;
    this.book = {
      title: '',
      category: '',
      isAvailable: false
    };
  }
}
<div>
  <div class="submit-form">
    <div *ngIf="!submitted">
      <div class="form-group">
        <label for="title">Title</label>
        <input
          type="text"
          class="form-control"
          id="title"
          required
          [(ngModel)]="book.title"
          name="title"
        />
      </div>

      <div class="form-group">
        <label for="category">Category</label>
        <input
          class="form-control"
          id="category"
          required
          [(ngModel)]="book.category"
          name="category"
        />
      </div>

      <button (click)="saveBook()" class="btn btn-success">Submit</button>
    </div>

    <div *ngIf="submitted">
      <h4>Book was submitted successfully!</h4>
      <button class="btn btn-success" (click)="newBook()">Add</button>
    </div>
  </div>
</div>
